json.messageString:
{
//Yolculuga basla
    state.location =  drive({start: state.baslangic, end: state.bitis,speed :600});

/*
//Gidilen Mesafe bilgisini yakala
    if (state.location)
    {
        state.kilometre += geolib.getDistance(
          { latitude: location.latitude, longitude: location.longitude },
          { latitude: state.location.latitude, longitude: state.location.longitude }
        );
    }
*/
//Her iterasyonda Yakit harca, onceki degerden max. 5 fark olsun
state.yakit-=chance.integer({min: 1, max:6, delta:3});

//Her iterasyonda hiz belirle
state.hiz1+=chance.integer({min: -5, max:5});

//Hava sicakligini kontrol et
state.sicaklik=weather.temperature({location: state.location, unit: 'c'});
if(state.yakit<=5){
    state.depodoldur++;
    state.yakit=100;
}
//Motor durum bilgisini guncelle
let veriblogu=({
        engine: "on",
        yakit: state.yakit,
        hiz1: state.hiz,
        arac: client(),
        serino:state.serino,
        plaka:state.plaka,
        depodoldur:state.depodoldur,
       // kilometre: state.kilometre,
        sicaklik:state.sicaklik,
        latitude: state.location.latitude,
        longitude: state.location.longitude

        });

//payload degerini JSON olarak dondur , Bu veriyi diger platformlardan yakalayip inceleyecegiz
    return JSON.stringify(veriblogu);
}

custom.initString:
{

// Rastgele Plaka olustur
    state.plaka = '06 '+
    chance.string({length: 2, pool: 'ABCDEFGHIJKLMNOPRSTUVYZ'})
    +' '+
    chance.string({length: 3, pool: '0123456789'});

//Aracin tekerlek hava basinci bilgisi, bunu yoldayken takip edecegiz.
    state.teker1basinc = 38;
    state.teker2basinc = 38;
    state.teker3basinc = 38;
    state.teker4basinc = 38;

//hava sicakligi bilgisi
    state.sicaklik = 25;

// Rastgele arac serinumarasi olustur
    state.serino = chance.string({length: 10, casing: 'upper', alpha: true, numeric: true}); 

//Yakit bilgisini veritabanindan oku, eger bilgi yoksa depoyu fulle
    state.yakit=glob.get(client()+'_yakitbilgisi');
    if (!state.yakit) state.yakit = 100;

//ilgili aracin baslangic koordinatini veritabanindan al
    state.baslangic=glob.get('arac_'+client()+'_baslangic');
    state.bitis=glob.get('Gazi_BE_Binasi');
//bir koordinat uydur ya da veritabanindan birtane oku
    if(!state.baslangic) state.baslangic=glob.get('cigerci_bahattin');

//Arac hizini tanimla:
state.hiz=100;

//depo doldurma sayisini tut. 
state.depodoldur=1;

}

